void DRV030_writeSector(	
    const uint8_t *buff,	
    uint32_t lba,		
    size_t count			
)
{
    size_t i;
    
    for(i=0; i<count; i++)
    {
        EmulatedDiskSectorWrite(&disk, (uint32_t)(lba + i), (uint8_t*)&buff[SECTOR_SIZE*i], true);
    }
    
    
}

void DRV030_readSector(	
    uint8_t *buff,	
    uint32_t lba,		
    size_t count			
)
{
    size_t i;
    
    for(i=0; i<count; i++)
    {
        EmulatedDiskSectorRead(&disk, (uint32_t)(lba + i), (uint8_t*)&buff[SECTOR_SIZE*i]);
    }
    
    
}

