static struct EMULATED_DISK disk = {0, NULL};

void DRV056_writeSector(const uint8_t *buff, uint32_t lba, size_t count)
{
    size_t i;
    
    for(i=0; i<count; i++){
        EmulatedDiskSectorWrite(&disk, (uint32_t)(lba + i), (uint8_t*)&buff[SECTOR_SIZE*i], true);
    }
    
    
}

void DRV056_readSector( uint8_t *buff, uint32_t lba, size_t count)
{
    size_t i;
    
    for(i=0; i<count; i++){
        EmulatedDiskSectorRead(&disk, (uint32_t)(lba + i), (uint8_t*)&buff[SECTOR_SIZE*i]);
    }
    
    
}

void DRV056_initialize(void)
{    
    EmulatedDiskCreate(&disk, SECTOR_SIZE);
    
    DRV056_writeSector((const uint8_t*)sector0, 0, 1);
    DRV056_writeSector((const uint8_t*)sector736, 736, 1);
    DRV056_writeSector((const uint8_t*)sector737, 737, 1);
    DRV056_writeSector((const uint8_t*)sector738, 738, 1);
    DRV056_writeSector((const uint8_t*)sector248, 248, 1);
    DRV056_writeSector((const uint8_t*)sector256, 256, 1);
    DRV056_writeSector((const uint8_t*)sector768, 768, 1);

}

void DRV056_print(void)
{
    EmulatedDiskPrint(&disk);
}

